import{a as r}from"./auth-client.t5zG2rRl.js";const a=document.getElementById("login-form"),e=document.getElementById("error-message");a.addEventListener("submit",async n=>{n.preventDefault();const o=document.getElementById("email").value,s=document.getElementById("password").value;e.style.display="none";try{const t=await r.signIn.email({email:o,password:s});t.error?(e.textContent=t.error.message||"Login failed",e.style.display="block"):window.location.href="/"}catch(t){e.textContent=t.message,e.style.display="block"}});
