import{a as r}from"./auth-client.t5zG2rRl.js";const c=document.getElementById("register-form"),e=document.getElementById("error-message"),s=document.getElementById("success-message");c.addEventListener("submit",async l=>{l.preventDefault();const o=document.getElementById("email").value,n=document.getElementById("password").value,a=document.getElementById("confirm-password").value;if(e.style.display="none",s.style.display="none",n!==a){e.textContent="Passwords do not match",e.style.display="block";return}try{const t=await r.signUp.email({email:o,password:n,name:o.split("@")[0],callbackURL:"/login"});t.error?(e.textContent=t.error.message||"Registration failed",e.style.display="block"):(s.textContent="Account created! Redirecting to login...",s.style.display="block",setTimeout(()=>{window.location.href="/login"},1500))}catch(t){e.textContent=t.message,e.style.display="block"}});
